export default function CurrentDate(props) {
   const days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
   ];
   let day = days[props.date.getDay()];
   let hours = props.date.getHours();
   let minutes = props.date.getMinutes();

   if (hours < 10) {
      hours = `0${hours}`;
   }
   if (minutes < 10) { 
      minutes = `0${minutes}`;
   }
   
   return `${day} ${hours}:${minutes}`;
}
