import React, { useState } from "react";
import axios from "axios";
import { ColorRing } from "react-loader-spinner";
import CurrentWeather from "./CurrentWeather";
import CurrentDate from "./CurrentDate";
import DailyForecast from "./DailyForecast";

export default function Weather(props) {
   const [weatherData, setWeatherData] = useState({ ready: false });
   const [city, setCity] = useState(props.defaultCity);

   function handleResponse(response) {
      setWeatherData({
         ready: true,
         city: response.data.name,
         date: new Date(response.data.dt * 1000),
         description: response.data.weather[0].description,
         temperature: response.data.main.temp,
         humidity: response.data.main.humidity,
         wind: response.data.wind.speed,
         icon: response.data.weather[0].icon,
         coord: response.data.coord,
      });
   }

   function search() {
      const apiKey = "5166d6bfe55bc81ec2d7bb78b538650f";
      const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
      axios.get(apiUrl).then(handleResponse);
   }

   function searchCity(e) {
      e.preventDefault();
      search();
   }

   function changeCity(e) {
      setCity(e.target.value);
   }

   if (weatherData.ready) {
      return (
         <div className="container p-5 pb-3 shadow rounded-4">
            <div className="Search">
               <form className="input-group" onSubmit={searchCity}>
                  <input
                     className="form-control input"
                     type="search"
                     placeholder="Enter a city"
                     onChange={changeCity}
                  />
                  <button className="btn btn-primary me-3" type="submit">
                     <i className="fa-solid fa-magnifying-glass"></i>
                  </button>
               </form>
            </div>
            <div className="city">
               <h2>{weatherData.city}</h2>
            </div>
            <p>
               <CurrentDate date={weatherData.date} />
            </p>
            <p className="weather-dscr">{weatherData.description}</p>
            <CurrentWeather data={weatherData} />
            <DailyForecast coordinates={weatherData.coord} />
         </div>
      );
   } else {
      search();
      return (
         <div className="spinner">
            <ColorRing
               visible={true}
               height="80"
               width="80"
               ariaLabel="blocks-loading"
               wrapperStyle={{}}
               wrapperClass="blocks-wrapper"
               colors={["#5c7aff", "#1ccad8", "#f8f7f9", "#f7ec59", "#ff66d8"]}
            />
         </div>
      );
   }
}
